第二题#include &lt;stdio.h>
int main()
&#123;
    int num[3][5];
    int min_row[3];
    int min_clm[5];
    int max;
    for (int i = 0; i &lt; 3; i++)
    &#123;
        for (int j = 0; j &lt; 5; j++)
        &#123;
            scanf("%d", &amp;num[i][j]);
        &#125;
    &#125;

    for (int i = 0; i &lt; 3; i++)
    &#123;
        int min = num[i][0];
        for (int j = 0; j &lt; 5; j++)
        &#123;
            min = min &lt; num[i][j] ? min : num[i][j];
        &#125;
        min_row[i] = min;
    &#125;
    for (int j = 0; j &lt; 5; j++)
    &#123;
        int min = num[0][j];
        for (int i = 0; i &lt; 3; i++)
        &#123;
            min = min &lt; num[i][j] ? min : num[i][j];
        &#125;
        min_clm[j] = min;
    &#125;

    max = min_row[0];
    for (int i = 0; i &lt; 3; i++)
    &#123;
        max = max > min_row[i] ? max : min_row[i];
    &#125;
    for (int j = 0; j &lt; 5; j++)
    &#123;
        max = max > min_clm[j] ? max : min_clm[j];
    &#125;

    printf("%d", max);

    return 0;
&#125;
第三题#include &lt;stdio.h>

void trans(int, char *);

int main()
&#123;
    int num[200];
    int n = 0;
    scanf("%d", &amp;num[n]);
    while (num[n] != -1)
    &#123;
        n++;
        scanf("%d", &amp;num[n]);
    &#125;
    char str[n][20];

    for (int i = 0; i &lt; n; i++)
    &#123;
        trans(num[i], str[i]);
    &#125;

    for (int i = 0; i &lt; n; i++)
    &#123;
        printf("%s ", str[i]);
    &#125;
    return 0;
&#125;

void trans(int num, char *str)
&#123;
    int num_rev = 0;
    int i = 0;
    while (num)
    &#123;
        num_rev = num_rev * 10 + num % 10;
        num /= 10;
    &#125;
    do
    &#123;
        int double_num = num_rev % 10 * 10 + num_rev % 100 / 10;
        if (double_num &lt; 26)
        &#123;
            str[i++] = 'A' + double_num;
            num_rev /= 100;
        &#125;
        else
        &#123;
            str[i++] = 'A' + num_rev%10;
            num_rev /= 10;
        &#125;
    &#125;while(num_rev);
    str[i] = 0;
    return;
&#125;
第四题#include &lt;stdio.h>

int sum(int *, int);

int main()
&#123;
    int num[5];
    for (int i = 0; i &lt; 5; i++)
    &#123;
        scanf("%d", &amp;num[i]);
    &#125;
    printf("%d", sum(num, 5));
    return 0;
&#125;

int sum(int *num, int n)
&#123;
    int sum = 0;
    for (int i = 0; i &lt; n; i++)
    &#123;
        sum += num[i];
    &#125;
    return sum;
&#125;
第五题#include &lt;stdio.h>
#include &lt;string.h>

struct fruit_info
&#123;
    int ID;
    char name[20];
    double sales;
    double price;
    double profit;
&#125;;

void set(struct fruit_info *);
void print(struct fruit_info);
void fruitcpy(struct fruit_info *, struct fruit_info *);
void swap(struct fruit_info *, struct fruit_info *);
void sort(struct fruit_info[], int);

int main()
&#123;
    struct fruit_info fruit[6];
    for (int i = 0; i &lt; 6; i++)
    &#123;
        set(&amp;fruit[i]);
    &#125;
    sort(fruit, 6);
    for (int i = 0; i &lt; 6; i++)
    &#123;
        print(fruit[i]);
    &#125;
    return 0;
&#125;
void set(struct fruit_info *fruit)
&#123;
    scanf("%d", &amp;fruit->ID);
    scanf("%s", fruit->name);
    scanf("%lf", &amp;fruit->sales);
    scanf("%lf", &amp;fruit->price);
    fruit->profit = fruit->sales * fruit->price;
    return;
&#125;
void print(struct fruit_info fruit)
&#123;
    printf("%d ", fruit.ID);
    printf("%s ", fruit.name);
    printf("%.2lf ", fruit.sales);
    printf("%.2lf ", fruit.price);
    printf("%.2lf\n", fruit.profit);
    return;
&#125;
void swap(struct fruit_info *a, struct fruit_info *b)
&#123;
    struct fruit_info tmp;
    tmp = *a;
    *a = *b;
    *b = tmp;
    return;
&#125;
void sort(struct fruit_info arr[], int len)
&#123;
    int i, j;
    for (i = 0; i &lt; len - 1; i++)
    &#123;
        int min = i;
        for (j = i + 1; j &lt; len; j++)
            if (arr[j].profit &lt; arr[min].profit)
                min = j;
        swap(&amp;arr[min], &amp;arr[i]);
    &#125;
&#125;
